import { useEffect, useState } from "react";

const useAxios = (url) => {
  const[data, setData] = useState(null);
  const [error, setError] = useState(null);

    userEffect(() => {
        const fetchData = async () => {
            try {
                const res = await fetch(url);
                const json = await res.json();
                setData(json);
              } catch (error) {
                setError(error);
              }
        }
        fetchData();
    },[url]);
  return {data, error};
}
export default useAxios;
