  const [error, setError] = useState(null);


const addData = async () => {
            try {
                const response = await axios.get(url);
                console.log(response.data)
                setData(d => [...d, response.data]);
              } catch (error) {
                setError(error);
              }
        }


    console.log(data)
    console.log(addData)
  return [data, addData, error];
}
